definition.script:
pipeline {
    agent { node { label 'node1' } }
    options {
        buildDiscarder(logRotator(numToKeepStr:'3'))
    }
	stages {
        stage('Clone repository') {
            steps {
                    deleteDir()
                    git url: 'https://github.com/nikolaikunai/jenkins.git', branch: 'main'
            }
		}
        stage('Check online hosts') {
            steps {
                sh """
                sudo apt-get update
                sudo apt-get install nmap -y
                nmap -sP -n -oN ./nmap_speedtest.txt 192.168.10.55-65
				"""
            }
        }
        stage('Check speed test'){
            steps {
                 sh """
                 echo "----------result of speedtest----------" >> ./nmap_speedtest.txt
                 speedtest-cli >> ./nmap_speedtest.txt
                 """
            }  
        }		
        stage('Push to branch') {
            steps {
                withCredentials([usernamePassword(credentialsId: 'github', usernameVariable: 'USER', passwordVariable: 'PASS')]) {
                    script {
                        env.encodedPass=URLEncoder.encode(PASS, "UTF-8")
                    }
                    sh """
                        git add .
						git commit -m "Add results file"
                        git push https://${USER}:${encodedPass}@github.com/nikolaikunai/jenkins.git main
					"""
					deleteDir()
			    }
            }
        }
	}		
}
